/**
 * Struttura dati relativa al contesto applicativo
 */
export interface ICjContext {
    id: string;
    application: string;
    environment: string;
    operatore: IOperatore;
    cliente: ICliente;
    canale: string;
}
/**
 * struttura dati contenente le informazioni dell'operatore collegato all'applicativo
 */
export interface IOperatore {
    abi: string;
    nome: string;
    cognome: string;
    filiale: string;
    ruoli: string[];
    userId: string;
}
/**
 * Struttura dati contenete le informazioni del cliente per cui l'operatore sta lavornado
 */
export interface ICliente {
    cellulare: string;
    codAzienda: string;
    codiceFiscale: string;
    codiceUic: string;
    comune?: string;
    eMail: string;
    fax: string;
    indirizzo: string;
    cap?: string;
    intestazione: string;
    nome?: string;
    cognome?: string;
    ndg: INDG;
    partitaIva: string;
    provincia: string;
    ramo: string;
    tipoNDG: string;
    telefono: string;
    settore: string;
    rapporti?: IRapporto[];
    tipoPersona?: string;
}
/**
 * Struttura dati ....
 */
export interface IRapporto {
    tipo: string;
    filiale?: string;
    numero?: string;
    stato?: string;
    formaTecnica?: string;
    codice?: string;
    dettaglio?: string;
    dataEstinzione?: string;
    dataScadenza?: string;
}
export interface INDG {
    ndg: string;
    ndgCollegati?: INDG[];
}
export interface ICjContextRequest {
    ndg: string;
    abi: string;
    codUnitaOperativa: string;
    application: string;
    canale: string;
    superGroup?: string[];
    aziendaGroup?: string[];
    filialeGroup?: string[];
}
