import { Observable } from 'rxjs/Observable';
export interface ISignatureDatasource {
    /**
     * metodo utilizzato per avere l'xml da firmare in caso di firma FEA
     * @return {Observable<string>} documenti da firmare in formato XML
     */
    getFeaXmlToSign(): Observable<string>;
    /**
     * metodo utilizzato per avere l'xml da firmare in caso di firma FDR
     * @return {Observable<string>} documenti da firmare in formato XML
     */
    getFdrXmlToSign(): Observable<string>;
    /**
     * metodo utilizzato per verificare lo stato della procedura di firma (polling)
     * @return {Observable<boolean>}
     */
    checkSignatureStatus(): Observable<boolean>;
}
