/** header personalizzabile del grafico */
export interface IChartHeader {
    /** pure text or HTML */
    content: string;
    /** testo tooltip opzionale  */
    tooltipContent?: string;
}
/** gruppo di barre che va ad occupare una stessa "corsia" del grafico */
export interface IChartBarGroup {
    /** id univoco opzionale */
    id?: string;
    /** array di barre da disporre una al termine della precedente */
    bars: Array<IChartBar>;
}
/**singola barra o anello del grafico */
export interface IChartBar {
    id?: string;
    /** legenda leggibile */
    label: string;
    /** colore CSS (es. red o #aa00bb) */
    color: string;
    /** valore */
    value: number;
    /** estremo inferiore del range, default 0 */
    min?: number;
    /** estremo superiore del range, default 100 */
    max?: number;
}
/** modello del grafico */
export interface NbpChartData {
    /** titolo del grafico */
    title: string;
    /** header personalizzati, non necessariamente corrispondenti in numero ai barGroup o bar */
    headers?: Array<IChartHeader>;
    /** gruppi di barre da visualizzare;
     * le barre nello stesso gruppo verranno disposte una di seguito alla precedente (stacked) */
    barGroups: Array<IChartBarGroup>;
    content?: string;
}
/** modello di rendering, dettaglio implementativo privato soggetto a cambiamenti */
export interface IChartBarView {
    id: string;
    label: string;
    color: string;
    value: number;
    stackOnValue: number;
    path: string;
    r: number;
    lane?: number;
    startx?: number;
    starty?: number;
    endx?: number;
    endy?: number;
}
