import { OnInit, EventEmitter } from '@angular/core';
import { NbpStyle } from './../nbp-commons/nbp-commons.enums';
export declare class NbpFilterComponent implements OnInit {
    /**
    * colore componente selettore
    * Valori possibili: check, default, outline, primary, profile, secondary
    */
    backgroundgray: NbpStyle;
    backgroundgreen: NbpStyle;
    check: NbpStyle;
    default: NbpStyle;
    outline: NbpStyle;
    primary: NbpStyle;
    profile: NbpStyle;
    secondary: NbpStyle;
    opened: boolean;
    /**
    * id univoco che identifica il selettore
    * Se nessun id è fornito e' auto-generato: select-1,select-2,select-3,.....
    */
    id: string;
    /**
    *
    * itemsView : array di json dei componenti della view
    *
    * var itemsView = [json1, json2, json3,......]
    *
    * Gli elemeti json dell'array possono essere di type: 'combo' o 'input'
    *
    *
    *  esempio Json Tipo Combo
    *  =======================
    *
    * {
    *   id :"selcombo-1",                  //opzionale    : identificatore univoco campo
    *   type: "combo",                     //obbligatorio : tipo selettore
    *   label: "label combo 1",            //opzionale    : label da visualizzare sul campo
    *   placeholder: "placeholder n. 1",   //opzionale    : placeholder campo
    *   required:false,                    //opzionale    : obbligatorietà input sul campo
    *   viewField:"description",           //obbligatorio : campo da adoperare nel drop-down del componente
    *   data: [
    *     {
    *       id: 11,                        //obbligatorio : identificatore univoco campo
    *       description: 'Item 1-1',       //campo che si è deciso di visualizzare nel drop down
    *       otherField: 'other Data 1-1'   //
    *     },
    *     {
    *       id: 12,                        //obbligatorio : identificatore univoco campo
    *       description: 'Item 1-2',       //campo che si è deciso di visualizzare nel drop down
    *       otherField: 'other Data 1-2',  //
    *     },
    *     .
    *     .
    *     .
    *     {
    *       ....................................................................................
    *       ....................................................................................
    *       ....................................................................................
    *     }
    * }
    *
    *
    *  esempio Json Tipo Input
    *  =======================
    * {
    *   id :"selinput-2",                  //opzionale    : identificatore univoco campo
    *   type: "input",                     //obbligatorio : tipo selettore
    *   label: "label input 2",            // label da visualizzare sul campo
    *   placeholder: "placeholder n. 2",   //opzionale    : label da visualizzare sul campo
    *   required:false,                    //opzionale    : obbligatorietà input sul campo
    *   data: null
    * }
    *
    */
    itemsView: any[];
    message: String;
    /**
     * Evento generato dal tasto conferma
     */
    searchEvent: EventEmitter<any>;
    /**
     * Evento generato dal tasto annulla
     */
    deleteEvent: EventEmitter<any>;
    itemsDataSource: any;
    selectedItems: any;
    labelVirgola: string;
    results: any;
    valoriFiltriSettati: string;
    constructor();
    ngOnInit(): void;
    toggle(): void;
    searchFields(): void;
    clearFields(): void;
    annulla(): void;
}
