/**
 * Struttura dati da utilizzare per definire gli Step del componente
 * nbp-step-navigabili
 */
export interface INavigationStep {
    name: string;
    selected: boolean;
    disabled: boolean;
    activated: boolean;
    content: string;
    disableClick?: boolean;
    onClick: any;
}
/**
 * Evento lanciato dal componente nbp-step-navigabili
 */
export interface INavigationStepEvent {
    index: number;
    data: INavigationStep;
}
