import { ICjContext } from './../../cj/cj-context/cj-context.interfaces';
import { IResolutionData } from './../../cj/cj-commons/cj-commons.interface';
/**
 * Informazioni di base necessarie per identificare gli errori
 */
export interface IBaseError {
    errorCode: string;
    errorSubCode?: string;
    errorMessage: string;
    errorSource: string;
    errorLevel?: string;
    stackTrace?: string;
}
/**
 * Informazioni presenti negli errori comunicati al BackEnd
 * dal FrontEnd
 */
export interface IClientError extends IBaseError {
    application?: string;
    version?: string;
    environment?: string;
    cjContext?: ICjContext;
    wizardContextId?: string;
    userAgent: string;
    screenResolution?: IResolutionData;
    windowResolution?: IResolutionData;
}
